    name: "workflow-example",
    mutingRulesHandling: "NOTIFY_ALL_ISSUES",
    issuesFilter: {
        name: "filter-name",
        type: "FILTER",
        predicates: [{
            attribute: "accumulations.tag.team",
            operator: "EXACTLY_MATCHES",
            values: ["growth"],
        }],
    },
    destinations: [{
        channelId: someChannel.id,
    }],
});
