    policyId: foo.id,
    name: "foo",
    type: "apm_kt_metric",
    entities: [txn.then(txn => txn.id)],
    metric: "error_percentage",
    runbookUrl: "https://www.example.com",
    terms: [{
        duration: 5,
        operator: "below",
        priority: "critical",
        threshold: 0.75,
        timeFunction: "all",
    }],
});
