func main() {
	pulumi.Run(func(ctx *pulumi.Context) error {
		_, err := cloud.NewAwsGovcloudLinkAccount(ctx, "foo", &cloud.AwsGovcloudLinkAccountArgs{
			AccountId:            pulumi.String("1234567"),
