    policyId: foo.id,
    name: "foo infra condition",
    description: "Warning if disk usage goes above 80% and critical alert if goes above 90%",
    type: "infra_metric",
    event: "StorageSample",
    select: "diskUsedPercent",
    comparison: "above",
    where: "(hostname LIKE '%frontend%')",
    critical: {
        duration: 25,
        value: 90,
        timeFunction: "all",
