    accountId: "12345678",
    name: "webhook-example",
    type: "WEBHOOK",
    destinationId: "00b6bd1d-ac06-4d3d-bd72-49551e70f7a8",
    product: "IINT",
    properties: [{
        key: "payload",
        value: "name: {{ foo }}",
        label: "Payload Template",
    }],
});
