		if err != nil {
			return err
		}
		_, err = newrelic.NewAlertPolicyChannel(ctx, "foo", &newrelic.AlertPolicyChannelArgs{
			PolicyId: pulumi.Any(newrelic_alert_policy.Example_policy.Id),
			ChannelIds: pulumi.IntArray{
				emailChannel.ID(),
				slackChannel.ID(),
			},
