import * as pulumi from "@pulumi/pulumi";
import * as newrelic from "@pulumi/newrelic";

const foo = new newrelic.cloud.AwsLinkAccount("foo", {
    arn: "arn:aws:service:region:account-id:resource-id",
    metricCollectionMode: "PUSH",
});
```
