import * as pulumi from "@pulumi/pulumi";
import * as newrelic from "@pulumi/newrelic";

const foobar = new newrelic.ApiAccessKey("foobar", {
    accountId: 1234567,
    ingestType: "LICENSE",
    keyType: "INGEST",
    notes: "To be used with service X",
});
```
