const foo = new newrelic.NrqlDropRule("foo", {
    accountId: 12345,
    action: "drop_data",
    description: "Drops all data for MyCustomEvent that comes from the LoadGeneratingApp in the dev environment, because there is too much and we dont look at it.",
    nrql: "SELECT * FROM MyCustomEvent WHERE appName='LoadGeneratingApp' AND environment='development'",
});
const bar = new newrelic.NrqlDropRule("bar", {
