});
const userOne = foo.then(foo => newrelic.getUser({
    authenticationDomainId: foo.id,
    name: "Test User",
}));
const userTwo = foo.then(foo => newrelic.getUser({
    authenticationDomainId: foo.id,
    emailId: "test_user@random.com",
}));
